exports.defineTags = function(dictionary) {
    dictionary.defineTag("preconditions", {
        mustHaveValue: true,
        onTagged: function(doclet, tag) {
          doclet.preconditions = doclet.where || [];
          doclet.preconditions.push(tag.value);
        }
    });
	dictionary.defineTag("postconditions", {
        mustHaveValue: true,
        onTagged: function(doclet, tag) {
          doclet.postconditions = doclet.where || [];
          doclet.postconditions.push(tag.value);
        }
    });
	dictionary.defineTag("invariant", {
        mustHaveValue: true,
        onTagged: function(doclet, tag) {
          doclet.invariant = doclet.where || [];
          doclet.invariant.push(tag.value);
        }
    });
	dictionary.defineTag("history", {
        mustHaveValue: true,
        onTagged: function(doclet, tag) {
          doclet.history = doclet.where || [];
          doclet.history.push(tag.value);
        }
    });
};